  <style>
    /* Minimal shadcn-like tokens so provided classes render correctly */
    :root{
  /* FORCE FULL BLACK BACKGROUND */
  --background: 0 0 0;

  /* biar tetap kebaca kalau sewaktu-waktu theme jadi "light" */
  --foreground: 248 250 252;
  --card: 10 10 10;
  --card-foreground: 248 250 252;
  --muted-foreground: 156 163 175;
  --border: 39 39 42;
  --accent: 248 250 252;
  --accent-bg: 255 255 255;
}
.dark{
  /* FULL BLACK juga */
  --background: 0 0 0;

  --foreground: 248 250 252;
  --card: 10 10 10;
  --card-foreground: 248 250 252;
  --muted-foreground: 156 163 175;
  --border: 39 39 42;
  --accent: 248 250 252;
  --accent-bg: 255 255 255;
}

    body { background-color: rgb(var(--background)); color: rgb(var(--foreground)); }

    /* Utility aliases used by your snippet */
    .bg-card\/60 { background-color: rgb(var(--card) / .60); }
    .text-card-foreground { color: rgb(var(--card-foreground)); }
    .text-muted-foreground { color: rgb(var(--muted-foreground)); }

    /* "accent" helpers used in nav classes */
    .bg-accent { background-color: rgb(var(--accent-bg) / .06); }
    .text-accent-foreground { color: rgb(var(--accent)); }

    /* Tailwind v4 style gradient alias used in your snippet */
    .bg-linear-to-r { background-image: linear-gradient(to right, var(--tw-gradient-stops)); }
    .bg-linear-to-b { background-image: linear-gradient(to bottom, var(--tw-gradient-stops)); }

    /* Make the card's absolute top line position correctly */
    [data-slot="card"] { position: relative; }
    [data-slot="card"]:hover > .pointer-events-none { opacity: 1 !important; }

    /* Classes present in your snippet that Tailwind CDN may not generate */
    .bg-white\/2 { background-color: rgba(255,255,255,.02); }
    .bg-\[\#3b82f6\]\/2 { background-color: rgba(59,130,246,.02); }
    .hover\:bg-brand-accent:hover { background-color: #2563eb; }

    /* 3D / motion helpers (from your snippet) */
    .perspective-1000 { perspective: 1000px; }
    .preserve-3d { transform-style: preserve-3d; }
    .rotate-3d { animation: pe-rotate3d 14s linear infinite; }
    .spin-reverse-slow { animation: pe-spinrev 10s linear infinite reverse; }
    .pulse-glow { animation: pe-pulse 2.8s ease-in-out infinite; }
    .float { animation: pe-float 6s ease-in-out infinite; }

    @keyframes pe-rotate3d {
      0%   { transform: rotateX(12deg) rotateY(18deg) rotateZ(0deg); }
      50%  { transform: rotateX(18deg) rotateY(210deg) rotateZ(0deg); }
      100% { transform: rotateX(12deg) rotateY(378deg) rotateZ(0deg); }
    }
    @keyframes pe-spinrev {
      0%   { transform: translateZ(0) rotateX(0deg) rotateY(0deg); }
      100% { transform: translateZ(0) rotateX(360deg) rotateY(360deg); }
    }
    @keyframes pe-pulse {
      0%,100% { opacity: .75; transform: scale(1); }
      50%     { opacity: 1; transform: scale(1.06); }
    }
    @keyframes pe-float {
      0%,100% { transform: translateY(0); }
      50%     { transform: translateY(-10px); }
    }

    ::selection{ background: rgba(59,130,246,.25); }
  </style>
